select date,abs from (select obs.date,abs(abs(obs.temp_mode) - abs(fcast.temp_fcast_mode)) from  (select date,mode() WITHIN GROUP (order by temp_c) as temp_mode from obs where  temp_c != 'NaN' group by date order by date desc limit 1) as obs, (select date,mode() WITHIN GROUP (order by temp) as temp_fcast_mode from fcast, (select date as sdate from obs order by date desc limit 1) as s where fcast.date >= sdate group by fcast.date) as fcast where obs.date=fcast.date) as a where a.abs >= 2.8;